import { Component,Input, OnInit } from '@angular/core';
import {FormControl, FormGroup, FormBuilder, Validators} from '@angular/forms'

@Component({
  selector: 'app-admin-login',
  templateUrl: './admin-login.component.html',
  styleUrls: ['./admin-login.component.css']
})
export class AdminLoginComponent implements OnInit{

  email: string = "";
  password: string = "";

  adminForm!: FormGroup;

  constructor(private formBuilder: FormBuilder){

    //This is showing the email and password visible
    this.adminForm = new FormGroup({
      email: new FormControl(this.email, [Validators.required]),
      password: new FormControl(this.password, [Validators.required])
    });
  }

  async adminLogin(){
    console.log(this.adminForm.value);
  }

  ngOnInit(){

  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Login</title>
  <!-- fontawesome cdn for icon -->
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.8.2/css/all.css" />
  <!-- main css file -->
  <link rel="stylesheet" href="admin-login.component.css">
</head>
<body>
<div class="container">

    <!-- form start -->
  <div class="my-form">
    <div class="form-title">
      <h1>Admin Login</h1>
    </div>
    <!-- main form -->
    <!-- You need the ReactiveFormModule and FormModule to be added to your app.module.ts -->
    <form class="text-center"  [formGroup]="adminForm" (ngSubmit)="adminLogin()">
      <div class="single-input">
        <span><i class="fas fa-user"></i></span>
        <input class="form-control" formControlName="email" type="email" name="email" placeholder="Email">
      </div>
      <div class="single-input">
        <span><i class="fas fa-unlock"></i></span>
        <input class="form-control" formControlName="password" type="password" placeholder="Password">
      </div>

      <div class="single-input submit-btn">
        <input type="submit" value="Login">
      </div>
    </form>

  </div>


</div>





</body>
</html>
