import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute } from '@angular/router';
import {FormControl, FormGroup, FormBuilder, Validators} from '@angular/forms'

@Component({
  selector: 'app-restaurant-login',
  templateUrl: './restaurant-login.component.html',
  styleUrls: ['./restaurant-login.component.css']
})
export class RestaurantLoginComponent implements OnInit {

  email: string= "";
  password: string = "";

  restaurantForm!: FormGroup;

  constructor(private formBuilder: FormBuilder, private router:Router){
    this.restaurantForm = new FormGroup({
      email: new FormControl(this.email,[Validators.required]),
      password: new FormControl(this.password, [Validators.required])
    });
  }

  async restaurantLogin(){
    console.log('hello world');
    this.router.navigate(['adminLogin']);

  }

  ngOnInit(){

  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <title>Login</title>
  <!-- fontawesome cdn for icon -->
  <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/5.8.2/css/all.css" />
  <!-- main css file -->
  <link rel="stylesheet" href="restaurant-login.component.css">
</head>
<body>
<div class="container">

    <!-- form start -->
  <div class="my-form">
    <div class="form-title">
      <h1>Restaurant Login </h1>
    </div>
    <!-- main form -->
    <form action= "" class="text-center" [formGroup]="restaurantForm" (ngSubmit)="restaurantLogin()">
      <div class="single-input">
        <span><i class="fas fa-user"></i></span>
        <input type="text" class="form-control" formControlName="email" name="email" placeholder="Email">
      </div>
      <div class="single-input">
        <span><i class="fas fa-unlock"></i></span>
        <input type="password" class="form-control" formControlName="password" placeholder="Password">
      </div>

      <div class="single-input submit-btn">
        <input type="submit" value="Login">
      </div>
    </form>

  </div>


</div>





</body>
</html>
